-- ----------------------------
-- Table structure for `fenzuxinxi`
-- ----------------------------
DROP TABLE IF EXISTS `fenzuxinxi`;
CREATE TABLE `fenzuxinxi` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `zulinid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '租赁id',
  `fangwuxinxiid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '房屋信息id',
  `zulindanhao` varchar(50) NOT NULL DEFAULT '' COMMENT '租赁单号',
  `fangwubianhao` varchar(50) NOT NULL DEFAULT '' COMMENT '房屋编号',
  `fangwubiaoti` varchar(255) NOT NULL DEFAULT '' COMMENT '房屋标题',
  `fangwuhuxing` varchar(50) NOT NULL DEFAULT '' COMMENT '房屋户型',
  `xiaoqumingcheng` varchar(50) NOT NULL DEFAULT '' COMMENT '小区名称',
  `fangwutupian` text NOT NULL COMMENT '房屋图片',
  `louceng` varchar(50) NOT NULL DEFAULT '' COMMENT '楼层',
  `mianji` varchar(50) NOT NULL DEFAULT '' COMMENT '面积',
  `fangwuzujin` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '房屋租金',
  `yajinfangshi` varchar(50) NOT NULL DEFAULT '' COMMENT '押金方式',
  `faburen` varchar(64) NOT NULL DEFAULT '' COMMENT '发布人',
  `leixing` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '类型',
  `jiagequjian` varchar(50) NOT NULL DEFAULT '' COMMENT '价格区间',
  `julixuexiao` varchar(50) NOT NULL DEFAULT '' COMMENT '距离学校',
  `zuiduanzuqi` int(11) NOT NULL DEFAULT '0' COMMENT '最短租期',
  `sheshi` text NOT NULL COMMENT '设施',
  `fangjianshu` varchar(50) NOT NULL DEFAULT '' COMMENT '房间数',
  `shifouyoudianti` varchar(50) NOT NULL DEFAULT '' COMMENT '是否有电梯',
  `zulinleixing` varchar(50) NOT NULL DEFAULT '' COMMENT '租赁类型',
  `fangwudizhi` varchar(255) NOT NULL DEFAULT '' COMMENT '房屋地址',
  `zulinshichang` int(11) NOT NULL DEFAULT '0' COMMENT '租赁时长',
  `zulinyonghu` varchar(64) NOT NULL DEFAULT '' COMMENT '租赁用户',
  `biaoti` varchar(255) NOT NULL DEFAULT '' COMMENT '标题',
  `fentanzujin` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '分摊租金',
  `lianxiren` varchar(50) NOT NULL DEFAULT '' COMMENT '联系人',
  `lianxifangshi` varchar(50) NOT NULL DEFAULT '' COMMENT '联系方式',
  `hezumiaoshu` longtext NOT NULL COMMENT '合租描述',
  `dianjilv` int(11) NOT NULL DEFAULT '0' COMMENT '点击率',
  `addtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '添加时间',
  `issh` varchar(10) NOT NULL DEFAULT '否' COMMENT '是否审核',
  PRIMARY KEY (`id`),
  KEY `zulin_index` (`zulinid`),
  KEY `fangwuxinxi_index` (`fangwuxinxiid`),
  KEY `leixing_index` (`leixing`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8mb4 COMMENT='分租信息';

